function random(max, min) {
	return	Math.floor(Math.random()*(max-min+1))+min;
}

function randomSentance(wordBank, puncBank, length) {
	var text = "";
	for (var i=0; i<length; i++) {
		text += wordBank[random(wordBank.length-1, 0)] + " "
	}
	text = text.substring(0,1).toUpperCase() + text.substring(1,text.length-1) + puncBank[random(puncBank.length-1,0)] + " " 
	return text
}

